function selectLanguage(languageSelect,otherLang,translated){var browserLanguage=window.navigator.userLanguage||window.navigator.language;var selectedLanguage=readCookie("language");if(browserLanguage!=null){browserLanguage=browserLanguage.substr(0,2)}if(selectedLanguage==null){if(browserLanguage!=null&&browserLanguage!=languageSelect&&browserLanguage==otherLang){window.location.replace(translated+".html")}}else{if(selectedLanguage!=languageSelect&&selectedLanguage==otherLang){window.location.replace(translated+".html")}}}function onLanguageChange(value){setCookie("language",value,365);return true}function saveLanguage(cookieValue){setCookie("language",cookieValue,365)}function setCookie(cookieName,cookieValue,nDays){var today=new Date;var expire=new Date;if(nDays==null||nDays==0)nDays=1;expire.setTime(today.getTime()+36e5*24*nDays);document.cookie=cookieName+"="+escape(cookieValue)+";expires="+expire.toGMTString()}function readCookie(name){var nameEQ=name+"=";var ca=document.cookie.split(";");for(var i=0;i<ca.length;i++){var c=ca[i];while(c.charAt(0)==" ")c=c.substring(1,c.length);if(c.indexOf(nameEQ)==0)return c.substring(nameEQ.length,c.length)}return null}